                        foreach (RoomItem Item in Session.GetHabbo().CurrentRoom.GetRoomItemHandler().FloorItems.Values)
                        {
                            if (Substances.IsAFood(Item) && RoleplayManager.Distance(new Vector2D(Item.X, Item.Y), new Vector2D(User.X, User.Y)) <= 1)
                            {
                                Food = Item;
                            }

