                                dbClient.AddParameter("oldowner", Session.GetHabbo().UserName);
                                dbClient.RunQuery();

                                Session.SendWhisper("Felicidades, haz cambiado tu nombre! Reinicia el hotel para ver los cambios.");

                            }
                            return true;
                        }
                        #endregion

                    }

